class BasicJSCache {
    constructor() {
        this.cache = {}
    }

    get(args, cb) {
        if (this.cache[args.key]) {
            if (Date.now() > this.cache[args.key].expires) {
                delete this.cache[args.key]
                return cb('expired cache key', null)
            } else {
                return cb(null, this.cache[args.key].value)
            }
        }
        return cb("cache key doesn't exist", null)
    }

    set(args, value) {
        this.cache[args.key] = {
            expires: args.ttl ? this.setExp(Date.now(), args.ttl) : null,
            value: value,
        }
    }

    setExp(date, ms) {
        return date + ms
    }

    flushCache(cb) {
        this.cache = {}
        // Promise/await compatibility.
        if (typeof cb === 'function') {
            cb(null, 'OK')
        }
    }
}

export default BasicJSCache

import LRUCache from 'lru-cache'

class _LRUCache {
    constructor(opts) {
        const options = Object.assign(
            {
                max: 500,
                length: function(n, key) {
                    return 1
                },
                dispose: function(key, n) {},
            },
            opts || {},
        )

        if (options.maxAge) {
            console.error(
                "[kayn] Do not use options.maxAge. It will not do anything. Rely on ttl's instead.",
            )
        }

        this.cache = LRUCache(options)
    }

    get(args, cb) {
        const blob = this.cache.get(args.key)
        return blob ? cb(null, blob) : cb("cache key doesn't exist", null)
    }

    set(args, value) {
        this.cache.set(args.key, value, args.ttl ? args.ttl : null)
    }

    flushCache(cb) {
        this.cache.reset()
        if (typeof cb === 'function') {
            cb(null, 'OK')
        }
    }
}

export default _LRUCache

import redis from 'redis'

// The set/get are pretty inefficient, but this will do for now.

class RedisCache {
    constructor(opts) {
        const options = Object.assign(
            {
                host: '127.0.0.1',
                port: 6379,
                keyPrefix: 'kayn-',
            },
            opts || {},
        )

        this.client = redis.createClient(options)

        // Should probably remove this...
        this.client.on('error', function(err) {
            console.log('Redis error:', err)
        })

        this.prefix = options.keyPrefix
    }

    get(args, cb) {
        this.client.get(this.prefix + args.key, (err, reply) => {
            reply ? cb(err, JSON.parse(reply)) : cb(err)
            return
        })
    }

    set(args, value) {
        this.client.setex(
            this.prefix + args.key,
            args.ttl,
            JSON.stringify(value),
        )
    }

    flushCache(cb) {
        this.client.flushdb(cb)
    }
}

export default RedisCache

